import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:3001/api'; // Replace with your API base URL

function lient()
{
    const [user, setUser] = useState(null);
    const [message, setMessage] = useState('');

    const handleSignup = async (phoneNumber, password) =>
    {
        try
        {
            const response = await axios.post(`${API_BASE_URL}/signup`, {
                phoneNumber,
                password,
            });
            setUser(response.data.user);
            setMessage(response.data.message);
        } catch (error)
        {
            setMessage(error.response.data.message);
        }
    };

    const handleLogin = async (phoneNumber, password) =>
    {
        try
        {
            const response = await axios.post(`${API_BASE_URL}/login`, {
                phoneNumber,
                password,
            });
            setUser(response.data.user);
            setMessage(response.data.message);
        } catch (error)
        {
            setMessage(error.response.data.message);
        }
    };

    const handleDeposit = async (amount) =>
    {
        try
        {
            const response = await axios.post(`${API_BASE_URL}/deposit`, {
                userId: user._id,
                amount,
            });
            setUser(response.data.user);
            setMessage(response.data.message);
        } catch (error)
        {
            setMessage(error.response.data.message);
        }
    };

    const handleWithdraw = async (amount) =>
    {
        try
        {
            const response = await axios.post(`${API_BASE_URL}/withdraw`, {
                userId: user._id,
                amount,
            });
            setUser(response.data.user);
            setMessage(response.data.message);
        } catch (error)
        {
            setMessage(error.response.data.message);
        }
    };

    const handleLogout = () =>
    {
        setUser(null);
        setMessage('');
    };

    return (
        <Router>
            <div className="App">
                <h1>Banking App</h1>
                <p>{message}</p>
                <Switch>
                    <Route exact path="/">
                        {!user ? (
                            <Redirect to="/login" />
                        ) : (
                            <>
                                <h2>Welcome, {user.phoneNumber}!</h2>
                                <p>Balance: {user.balance}</p>
                                <button onClick={handleLogout}>Logout</button>
                                <TransactionHistory transactions={user.transactions} />
                            </>
                        )}
                    </Route>
                    <Route path="/signup">
                        {user ? (
                            <Redirect to="/" />
                        ) : (
                            <SignupForm handleSignup={handleSignup} />
                        )}
                    </Route>
                    <Route path="/login">
                        {user ? (
                            <Redirect to="/" />
                        ) : (
                            <LoginForm handleLogin={handleLogin} />
                        )}
                    </Route>
                    <Route path="/deposit">
                        {!user ? (
                            <Redirect to="/login" />
                        ) : (
                            <DepositForm handleDeposit={handleDeposit} />
                        )}
                    </Route>
                    <Route path="/withdraw">
                        {!user ? (
                            <Redirect to="/login" />
                        ) : (
                            <WithdrawForm handleWithdraw={handleWithdraw} />
                        )}
                    </Route>
                </Switch>
            </div>
        </Router>
    );
}

function SignupForm({ handleSignup })
{
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) =>
    {
        e.preventDefault();
        handleSignup(phoneNumber, password);
    };

    return (
        <div>
            <h2>Signup</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Phone Number"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Signup</button>
            </form>
        </div>
    );
}

function LoginForm({ handleLogin })
{
    const [phoneNumber, setPhoneNumber] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = (e) =>
    {
        e.preventDefault();
        handleLogin(phoneNumber, password);
    };

    return (
        <div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    placeholder="Phone Number"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <button type="submit">Login</button>
            </form>
        </div>
    );
}

function DepositForm({ handleDeposit })
{
    const [amount, setAmount] = useState('');

    const handleSubmit = (e) =>
    {
        e.preventDefault();
        handleDeposit(parseFloat(amount));
    };

    return (
        <div>
            <h2>Deposit</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    placeholder="Amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                />
                <button type="submit">Deposit</button>
            </form>
        </div>
    );
}

function WithdrawForm({ handleWithdraw })
{
    const [amount, setAmount] = useState('');

    const handleSubmit = (e) =>
    {
        e.preventDefault();
        handleWithdraw(parseFloat(amount));
    };

    return (
        <div>
            <h2>Withdraw</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    placeholder="Amount"
                    value={amount}
                    onChange={(e) => setAmount(e.target.value)}
                />
                <button type="submit">Withdraw</button>
            </form>
        </div>
    );
}

function TransactionHistory({ transactions })
{
    return (
        <div>
            <h2>Transaction History</h2>
            <ul>
                {transactions.map((transaction) => (
                    <li key={transaction._id}>
                        Type: {transaction.type}, Amount: {transaction.amount}
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
