import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './login.css'
const LoginPage = () =>
{
    const Navigate = useNavigate();
    const [formData, setFormData] = useState({
        phoneNumber: '',
        password: '',
    });
    const [errorMessage, setErrorMessage] = useState('');

    const handleChange = (e) =>
    {
        const { name, value } = e.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) =>
    {
        e.preventDefault();

        try
        {
            // Send login request to the backend API
            const response = await fetch('https://olaitan01.onrender.com/users/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });
const responseData = await response.json();
            if (response.ok)
            {
                const { _id, token } = responseData; // Extract the userId and token from the response data
                window.localStorage.setItem('userId', _id); // Save the userId to localStorage
                window.localStorage.setItem('token', token); // Save the token to localStorage
                // Login successful
                console.log('Login successful:', responseData);
                // Redirect to the dashboard or perform any other actions
                Navigate('/dashboard')
            } else
            {
                // Login failed
                const errorData = responseData;
                console.log('Login failed:', errorData.message);

                // Display the error message
                setErrorMessage(errorData.message);
            }
        } catch (error)
        {
            console.error('Error during login:', error);
        }
    };

    return (
        <div className='login-main'>
            {errorMessage && <p>{errorMessage}</p>}
            <form onSubmit={handleSubmit} className='login-form'>

                <label>
                    Phone Number:
                    <input
                        type="text"
                        name="phoneNumber"
                        value={formData.phoneNumber}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <label>
                    Password:
                    <input
                        type="password"
                        name="password"
                        value={formData.password}
                        onChange={handleChange}
                    />
                </label>
                <br />
                <button type="submit">Log In</button>
            </form>
        </div>
    );
};

export default LoginPage;
