import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const History = () =>
{
    const Navigate = useNavigate();
    const [transactions, setTransactions] = useState([]);
    const userId = localStorage.getItem('id');

    useEffect(() =>
    {
        const fetchTransactionHistory = async () =>
        {
            try
            {
                // Fetch the user's transaction history from the backend API
                const response = await fetch(`https://olaitan01.onrender.com/users/${userId}/history`, {
                    method: 'GET',
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
                    },
                });

                if (!response.ok)
                {
                    throw new Error('Error fetching transaction history');
                }

                const data = await response.json();

                // Check if data is an array
                if (Array.isArray(data))
                {
                    // Set the fetched transaction data to the state
                    setTransactions(data);
                } else
                {
                    console.error('Invalid transaction data:', data);
                }
            } catch (error)
            {
                console.error('Error fetching transaction history:', error);
            }
        };

        fetchTransactionHistory();
    }, [userId]);

    return (
        <div>
            <h2>Transaction History</h2>
            <table>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Amount</th>
                        <th>Type</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((transaction) => (
                        <tr key={transaction._id}>
                            <td>{transaction.date}</td>
                            <td>{transaction.amount}</td>
                            <td>{transaction.type}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div>
                <button type="button" onClick={() => Navigate('/dashboard')}>
                    Back to Dashboard
                </button>
            </div>
        </div>
    );
};

export default History;
