import React, { useState, useEffect } from 'react';
import DepositPage from '../../components/deposit/deposit';
import WithdrawalPage from '../../components/withdrawal/withdrawal';
import Balance from '../../components/balance/balance';

const Dashboard = () =>
{
    const [transactions, setTransactions] = useState([]);
    const userId = localStorage.getItem('id');
    consol
    useEffect(() =>
    {
        // Fetch the user's transaction history from the backend API
        fetch(`https://olaitan01.onrender.com/users/${userId}/history`, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
            },
        })
            .then((response) => response.json())
            .then((data) =>
            {
                // Check if data is an array
                if (Array.isArray(data))
                {
                    // Set the fetched transaction data to the state
                    setTransactions(data);
                    console.log(data);
                } else
                {
                    console.error('Invalid transaction data:', data);
                }
            })
            .catch((error) =>
            {
                console.error('Error fetching transaction history:', error);
            });
    }, [userId]);

    return (
        <div>
            <div>
                <h2>Balance</h2>
              <Balance/>

            </div>

            <h2>Transaction History</h2>
            <table>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Amount</th>
                        <th>Type</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((transaction) => (
                        <tr key={transaction.id}>
                            <td>{transaction.date}</td>
                            <td>{transaction.amount}</td>
                            <td>{transaction.type}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <div>
                <button onClick={DepositPage}>Deposit</button>
                <button onClick={WithdrawalPage}>Withdraw</button>
            </div>
        </div>
    );
};

export default Dashboard;
