import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './signup.css'
const SignupPage = () =>
{
    const Navigate = useNavigate();
    const [formData, setFormData] = useState({
        phoneNumber: '',
        password: '',
    });
    const [successMessage, setSuccessMessage] = useState('');

    const handleChange = (e) =>
    {
        const { name, value } = e.target;
        setFormData((prevFormData) => ({
            ...prevFormData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) =>
    {
        e.preventDefault();

        try
        {
            // Send signup request to the backend API
            const response = await fetch('https://olaitan01.onrender.com/users', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });

            if (response.ok)
            {
                // Signup successful
                const userData = await response.json();
                console.log('Signup successful:', userData);

                // Display the error message
                // Redirect to the login page or perform any other actions
                Navigate('/login');
            } else
            {
                // Signup failed
                const errorData = await response.json();
                console.log('Signup failed:', errorData.message);
            }
        } catch (error)
        {
            console.error('Error during signup:', error);
        }
    };

    return (
        <React.Fragment >
            <div className='main-div'>
                <h1>SignUp</h1>
                <form onSubmit={handleSubmit} className='form'>
                    {successMessage && <p>{successMessage}</p>}
                    <label>
                        Phone Number:
                        <input
                            type="text"
                            placeholder='Enter a valid phone number'
                            name="phoneNumber"
                            value={formData.phoneNumber}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <label>
                        Password:
                        <input
                            type="password"
                            placeholder='Enter a password'
                            name="password"
                            value={formData.password}
                            onChange={handleChange}
                        />
                    </label>
                    <br />
                    <button type="submit" className='btn01'>Sign Up</button>
                    <br/>
                    <button type='button'className='btn02' onClick={() => Navigate('/login')}>Login</button>
                </form>
            </div>
        </React.Fragment>
    );
};

export default SignupPage;
