const Signup = () =>
{
    const { handleSignup } = useContext(DataContext) ;
    const [confirmPassword, setConfirmPass] = useState("")
    const [formData, setFormData] = useState({
       phoneNumber: "",
        password: "",
    })
    const handleChange = (e) =>
    {
        e.preventDefault();
        const { name, value } = e.target;
        setFormData(prev =>
        {
            return { ...prev, [name]: value }
        });
    }

  return (
      <div className={style["login-container"]}>
          Signup
          <FormInput type="phone" placeholder="phone" value={formData.phoneNumber} onChange={handleChange} name="phone" />
          <FormInput type="password" placeholder="password" value={formData.password} onChange={handleChange} name="password" />
          <AuthButton text='Submit' onClick={() => handleSignup(formData)} />
      </div>
  )
}

export default Signup
