import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
const Balance = () =>
{
    const Navigate = useNavigate()
    const [balance, setBalance] = useState('');
    const userId = localStorage.getItem('userId');
console.log(userId)
    useEffect(() =>
    {
        // Fetch the user's balance from the backend API
        fetch(`https://olaitan01.onrender.com/users/${userId}`, {
            method: 'GET',
            headers: {
                Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
            },
        })
            .then((response) => response.json())
            .then((data) =>
            {
                console.log(data)
                // Set the balance state with the fetched data
                setBalance(data.balance);
            })
            .catch((error) =>
            {
                console.error('Error fetching balance:', error);
            });
    }, [userId]);

    return (
        <div>
            <h2>Your Balance is:</h2>
            <p>â‚¦{balance}</p>
            <div>
            <button type='' onClick={() => Navigate('/Deposit')}>Deposit</button>
             <button type='' onClick={() => Navigate('/Withdraw')}>Withdraw</button>
            <button type='' onClick={() => Navigate('')}>View Transaction History</button>

          </div>
        </div>
    );
};

export default Balance;
