import React, { useState, useEffect } from 'react';

const DepositPage = () =>
{
    const [amount, setAmount] = useState('');

    const handleAmountChange = (e) =>
    {
        setAmount(e.target.value);
    };

    const handleDeposit = async () =>
    {
        try
        {
            const userId = localStorage.getItem('userId');
            console.log(userId);

            const depositFund = await fetch(`https://olaitan01.onrender.com/users/${userId}/deposit`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
                },
                body: JSON.stringify({ amount }),
            });

            const depositData = await depositFund.json();
            console.log('Deposit successful:', depositData);
            // Perform any additional actions or show success message
        } catch (error)
        {
            console.error('Error during deposit:', error);
            // Handle deposit error
            // Show error message or perform any error handling
        }
    };

    useEffect(() =>
    {
        // Call the deposit function when the component mounts
        handleDeposit();
    }, [amount]);

    return (
        <div>
            <h2>Deposit</h2>
            <label>
                Amount:
                <input type="number" value={amount} onChange={handleAmountChange} />
            </label>
            <br />
            <button onClick={handleDeposit}>Deposit</button>
        </div>
    );
};

export default DepositPage;
