import React, { useState, useEffect } from 'react';

const DepositPage = () =>
{
    const [amount, setAmount] = useState('');
    const handleAmountChange = (e) =>
    {
        setAmount(e.target.value);
    };

    const handleDeposit = async () =>
    {
        const userId = localStorage.getItem('userId');
        console.log(userId)
        // Send deposit request to the backend API
        useEffect(() =>
        {
            fetch(`https://olaitan01.onrender.com/users/${userId}/history`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
                },
            })
                .then((response) => response.json())
                .then((data) =>
                {
                    // Check if data is an array
                    if (Array.isArray(data))
                    {
                        // Set the fetched transaction data to the state
                        setTransactions(data);
                        console.log(data);
                    } else
                    {
                        console.error('Invalid transaction data:', data);
                    }
                })
                .catch((error) =>
                {
                    console.error('Error fetching transaction history:', error);
                });

            // Deposit funds
            const depositFunds = async () =>
            {
                try
                {
                    const depositFund = await fetch(`https://olaitan01.onrender.com/users/${userId}/deposit`, {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                            Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
                        },
                        body: JSON.stringify({ amount }),
                    });
                    const depositData = await depositFund.json();
                    console.log('Deposit successful:', depositData);
                    // Perform any additional actions or show success message
                } catch (error)
                {
                    console.error('Error during deposit:', error);
                    // Handle deposit error
                    // Show error message or perform any error handling
                }
            };

            // Call the deposit funds function
            depositFunds();
        }, [userId]);
    }

    return (
        <div>
            <h2>Deposit</h2>
            <label>
                Amount:
                <input
                    type="number"
                    value={amount}
                    onChange={handleAmountChange}
                />
            </label>
            <br />
            <button onClick={handleDeposit}>Deposit</button>
        </div>
    );
};

export default DepositPage;
