import React, { useState } from 'react';

const WithdrawPage = () =>
{
    const [amount, setAmount] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleAmountChange = (e) =>
    {
        setAmount(e.target.value);
    };

    const handleWithdraw = async () =>
    {
        try
        {
            const userId = localStorage.getItem('userId');
            const withdrawResponse = await fetch(`https://olaitan01.onrender.com/users/${userId}/withdraw`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
                },
                body: JSON.stringify({ amount }),
            });

            if (withdrawResponse.ok)
            {
                const withdrawData = await withdrawResponse.json();
                console.log('Withdrawal successful:', withdrawData);
                const successful = withdrawData;

                // Display the error message
                setSuccessMessage(successful.message);
                // Perform any additional actions or show success message
            } else
            {
                console.error('Withdrawal failed:', withdrawResponse.status);
                // Handle withdraw failure
                // Show error message or perform any error handling

            }
        } catch (error)
        {
            console.error('Error during withdrawal:', error);
            // Handle withdraw error
            // Show error message or perform any error handling
        }
    }


    return (
        <div>
            {successMessage && <p>{successMessage}</p>}

            <h2>Withdraw</h2>
            <label>
                Amount:
                <input type="number" value={amount} onChange={handleAmountChange} />
            </label>
            <br />
            <button onClick={handleWithdraw}>Withdraw</button>
            <div>

                <button type='' onClick={() => Navigate('/daschboard')}>Back to Dashboard</button>

            </div>
        </div>
    );

}
export default WithdrawPage;
