import React, { useState, useEffect } from 'react';

const DashboardPage = () =>
{
    const [balance, setBalance] = useState(0);

    useEffect(() =>
    {
        // Fetch the user's balance from the backend API
        fetch('https://olaitan01.onrender.com/users/:id/history', {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token')}`,
            },
        })
            .then((response) => response.json())
            .then((data) =>
            {
                // Update the balance state
                setBalance(data.balance);
            })
            .catch((error) =>
            {
                console.error('Error fetching balance:', error);
            });
    }, []);

    const handleDeposit = () =>
    {
        // Logic to handle deposit
        console.log('Deposit button clicked');
    };

    const handleWithdrawal = () =>
    {
        // Logic to handle withdrawal
        console.log('Withdrawal button clicked');
        Navigate('/withdraw');
    };

    return (
        <div>
            <h2>Dashboard</h2>
            <p>Balance: ${balance}</p>
            <button onClick={handleDeposit}>Deposit</button>
            <button onClick={handleWithdrawal}>Withdrawal</button>
        </div>
    );
};

export default DashboardPage;
