import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'
im
const DepositPage = () =>
{
    const Navigate = useNavigate()
    const [amount, setAmount] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleAmountChange = (e) =>
    {
        setAmount(e.target.value);
    };

    const handleDeposit = async () =>
    {
        try
        {
            const userId = localStorage.getItem('userId');
            const depositResponse = await fetch(`https://olaitan01.onrender.com/users/${userId}/deposit`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    Authorization: `Bearer ${localStorage.getItem('id', userId)}`,
                },
                body: JSON.stringify({ amount }),
            });

            if (depositResponse.ok)
            {
                const depositData = await depositResponse.json();
                console.log('Withdrawal successful:', depositData);
                const successful = depositData;

                // Display the error message
                setSuccessMessage(successful.message);
            } else
            {
                console.error('Deposit failed:', depositResponse.status);
                // Handle deposit failure
                // Show error message or perform any error handling
            }
        } catch (error)
        {
            console.error('Error during deposit:', error);
            // Handle deposit error
            // Show error message or perform any error handling
        }
    }


    return (
        <div>
            {successMessage && <p>{successMessage}</p>}

            <h2>Deposit</h2>
            <label>
                Amount:
                <input type="number" value={amount} onChange={handleAmountChange} />
            </label>
            <br />
            <button onClick={handleDeposit}>Deposit</button>
            <div>

                <button type='' onClick={() => Navigate('/dashboard')}>Back to Dashboard</button>

            </div>
        </div>

    );

}
export default DepositPage;
