import React, { useState, useEffect } from 'react';
import DepositPage from '../../components/deposit/deposit';
import WithdrawalPage from '../../components/withdrawal/withdrawal';
import Balance from '../../components/balance/balance';

const Dashboard = () =>
{
    const [transactions, setTransactions] = useState([]);
    // Implement a function to extract user ID from the token

    const getUserIdFromToken = (token) =>
    {
        try
        {
            const decodedToken = this.jwtService.verify(token);
            const userId = decodedToken.sub; // Assuming the user ID is stored in the 'sub' field of the token payload
            return userId;
        } catch (error)
        {
            // Handle token verification error
            throw new Error('Invalid token');
        }
    }
    const userId = getUserIdFromToken(); 
    useEffect(() =>
    {
        // Fetch the user's transaction history from the backend API
        fetch(`https://olaitan01.onrender.com/users/${userId}/history`, {
            headers: {
                Authorization: `Bearer ${localStorage.getItem('token', userId)}`,
            },
        })
            .then((response) => response.json())
            .then((data) =>
            {
                // Set the fetched transaction data to the state
                setTransactions(data);
                console.log(data);
            })
            .catch((error) =>
            {
                console.error('Error fetching transaction history:', error);
            });
    }, [user]);

    return (
        <div>
            <div>
                <h2>Balance</h2>
              <Balance/>

            </div>
            <div>
                <h2>Deposit</h2>
                <DepositPage/>
            </div>
            <div>
                <h2>Withdrawal</h2>
                <WithdrawalPage />
            </div>
            <h2>Transaction History</h2>
            <table>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Amount</th>
                        <th>Type</th>
                    </tr>
                </thead>
                <tbody>
                    {transactions.map((transaction) => (
                        <tr key={transaction.id}>
                            <td>{transaction.date}</td>
                            <td>{transaction.amount}</td>
                            <td>{transaction.type}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
